// Sistema centralizado de manejo de errores en Express (Node.js)

// 1. Definir clases de error personalizadas
class AppError extends Error {
  constructor(message, statusCode, errorCode = null) {
    super(message);
    this.statusCode = statusCode;
    this.errorCode = errorCode || `ERR_${statusCode}`;
    this.isOperational = true; // Errores operacionales vs. programaci칩n

    Error.captureStackTrace(this, this.constructor);
  }
}

// Errores concretos para casos espec칤ficos
class NotFoundError extends AppError {
  constructor(message = 'Recurso no encontrado') {
    super(message, 404, 'ERR_NOT_FOUND');
  }
}

class ValidationError extends AppError {
  constructor(message = 'Datos de solicitud inv치lidos', errors = []) {
    super(message, 400, 'ERR_VALIDATION');
    this.errors = errors;
  }
}

class UnauthorizedError extends AppError {
  constructor(message = 'No autorizado') {
    super(message, 401, 'ERR_UNAUTHORIZED');
  }
}

class ForbiddenError extends AppError {
  constructor(message = 'Acceso prohibido') {
    super(message, 403, 'ERR_FORBIDDEN');
  }
}

class ConflictError extends AppError {
  constructor(message = 'Conflicto con el estado actual') {
    super(message, 409, 'ERR_CONFLICT');
  }
}

class InternalError extends AppError {
  constructor(message = 'Error interno del servidor') {
    super(message, 500, 'ERR_INTERNAL');
  }
}

// 2. Middleware para capturar errores espec칤ficos (404)
const notFoundHandler = (req, res, next) => {
  next(new NotFoundError(`No se encontr칩 la ruta: ${req.originalUrl}`));
};

// 3. Middleware para validaci칩n de entrada (ejemplo con express-validator)
const validate = (validations) => {
  return async (req, res, next) => {
    await Promise.all(validations.map(validation => validation.run(req)));

    const errors = validationResult(req);
    if (errors.isEmpty()) {
      return next();
    }

    const extractedErrors = errors.array().map(err => ({
      field: err.param,
      message: err.msg
    }));

    next(new ValidationError('Error de validaci칩n', extractedErrors));
  };
};

// 4. Middleware manejador de errores centralizado
const errorHandler = (err, req, res, next) => {
  // Establecer valores por defecto para error no operacional
  let error = err;

  // Si es un error no controlado, convertirlo a AppError
  if (!err.isOperational) {
    // Errores espec칤ficos de MongoDB/Mongoose
    if (err.name === 'CastError') {
      error = new NotFoundError(`Recurso no v치lido: ${err.value}`);
    } else if (err.code === 11000) {
      const field = Object.keys(err.keyValue)[0];
      error = new ConflictError(`El ${field} ya existe. Por favor use otro valor.`);
    } else if (err.name === 'ValidationError') {
      const errors = Object.values(err.errors).map(val => ({
        field: val.path,
        message: val.message
      }));
      error = new ValidationError('Datos inv치lidos proporcionados', errors);
    } else {
      // Error desconocido
      console.error('ERROR NO MANEJADO 游눤:', err);
      error = new InternalError();
    }
  }

  // Logging de desarrollo vs producci칩n
  if (process.env.NODE_ENV === 'development') {
    console.error('Error Stack:', err.stack);
  } else {
    // En producci칩n, loguear en un servicio estructurado como Sentry/Loggly
    // logger.error({ err, requestId: req.id, user: req.user?.id });
  }

  // Estructurar respuesta de error
  const errorResponse = {
    status: 'error',
    code: error.errorCode,
    message: error.message
  };

  // Incluir detalles adicionales para errores de validaci칩n
  if (error instanceof ValidationError && error.errors) {
    errorResponse.errors = error.errors;
  }

  // Incluir stack trace solo en desarrollo
  if (process.env.NODE_ENV === 'development') {
    errorResponse.stack = err.stack;
  }

  // Enviar respuesta
  res.status(error.statusCode || 500).json(errorResponse);
};

// 5. Configuraci칩n en la aplicaci칩n Express
const express = require('express');
const app = express();

// Middleware y rutas
