// 6. Uso en controladores
const getUser = async (req, res, next) => {
  try {
    const user = await User.findById(req.params.id);

    if (!user) {
      // Usar clases de error personalizadas
      throw new NotFoundError('Usuario no encontrado');
    }

    res.status(200).json({
      status: 'success',
      data: { user }
    });
  } catch (error) {
    // Pasar el error al middleware centralizado
    next(error);
  }
};

// 7. Error handling para promesas no gestionadas y excepciones
process.on('unhandledRejection', (err) => {
  console.error('UNHANDLED REJECTION! 游눤 Shutting down...');
  console.error(err.name, err.message, err.stack);
  // En producci칩n, considerar el cierre ordenado de la aplicaci칩n
  // server.close(() => {
  //   process.exit(1);
  // });
});

process.on('uncaughtException', (err) => {
  console.error('UNCAUGHT EXCEPTION! 游눤 Shutting down...');
  console.error(err.name, err.message, err.stack);
  // Para excepciones no capturadas, generalmente es mejor cerrar el proceso
  process.exit(1);
});

// Configuraci칩n de logging con Winston en Node.js
const winston = require('winston');
const { createLogger, format, transports } = winston;
const { combine, timestamp, printf, colorize, json } = format;

// Middleware para generar ID 칰nico para cada solicitud
const { v4: uuidv4 } = require('uuid');
const requestId = require('express-request-id')();

// Formateador personalizado para desarrollo (legible por humanos)
const devLogFormat = printf(({ level, message, timestamp, ...metadata }) => {
  let metaStr = '';

  if (metadata.requestId) {
    metaStr += `[${metadata.requestId}] `;
  }

  if (metadata.userId) {
    metaStr += `[User: ${metadata.userId}] `;
  }

  if (metadata.path) {
    metaStr += `[${metadata.method} ${metadata.path}] `;
  }

  // A침adir otros metadatos como objeto JSON si existen
  const otherMeta = { ...metadata };
  delete otherMeta.requestId;
  delete otherMeta.userId;
  delete otherMeta.path;
  delete otherMeta.method;

  if (Object.keys(otherMeta).length > 0) {
    metaStr += JSON.stringify(otherMeta);
  }

  return `${timestamp} ${level}: ${message} ${metaStr}`;
});

// Crear logger con configuraci칩n diferente seg칰n entorno
const logger = createLogger({
  level: process.env.NODE_ENV === 'production' ? 'info' : 'debug',
  defaultMeta: { service: 'api-service' },
  transports: [
    // Siempre registrar a la consola
    new transports.Console({
      format: combine(
        colorize(),
        timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
        process.env.NODE_ENV === 'production'
          ? json() // JSON en producci칩n para parsear f치cilmente
          : devLogFormat // Formato legible en desarrollo
      )
    })
  ]
});

// En producci칩n, a침adir transporte de archivo con rotaci칩n
if (process.env.NODE_ENV === 'production') {
  // Require solo en producci칩n para no a침adir dependencias innecesarias en desarrollo
  const { DailyRotateFile } = require('winston-daily-rotate-file');

  // Archivo para todos los logs
  logger.add(new DailyRotateFile({
    filename: 'logs/application-%DATE%.log',
    datePattern: 'YYYY-MM-DD',
    maxSize: '20m',
    maxFiles: '14d',
    format: combine(
      timestamp(),
      json()
    )
  }));

  // Archivo separado solo para errores
  logger.add(new DailyRotateFile({
    filename: 'logs/error-%DATE%.log',
    datePattern: 'YYYY-MM-DD',
    level: 'error',
    maxSize: '20m',
    maxFiles: '30d',
    format: combine(
      timestamp(),
      json()
    )
  }));
}

// Crear middleware para logging de solicitudes HTTP
const loggerMiddleware = (req, res, next) => {
  // A침adir ID 칰nico a cada solicitud
  if (!req.id) {
    req.id = uuidv4();
    res.setHeader('X-Request-ID', req.id);
  }

  // Registrar inicio de solicitud
  logger.info(`${req.method} ${req.originalUrl}`, {
    requestId: req.id,
    userId: req.user?.id,
    method: req.method,
    path: req.originalUrl,
    ip: req.ip,
    userAgent: req.get('User-Agent')
  });

  // Registrar tiempo de respuesta al finalizar
  const startTime = Date.now();
  res.on('finish', () => {
    const duration = Date.now() - startTime;
    const level = res.statusCode >= 400 ? 'warn' : 'info';

    logger[level](`${req.method} ${req.originalUrl} ${res.statusCode} - ${duration}ms`, {
      requestId: req.id,
      userId: req.user?.id,
      method: req.method,
      path: req.originalUrl,
      statusCode: res.statusCode,
      duration,
      contentLength: res.get('Content-Length')
    });
  });

  next();
};

// Funci칩n para sanitizar datos sensibles antes de loguear
const sanitizeData = (data) => {
  if (!data) return data;

  // Crear copia profunda para no modificar original
  const sanitized = JSON.parse(JSON.stringify(data));

  // Lista de campos sensibles a ocultar
  const sensitiveFields = [
    'password', 'passwordHash', 'token', 'secret', 
    'authorization', 'cardNumber', 'cvv', 'ssn',
    'creditCard', 'socialSecurityNumber'
  ];

  const sanitizeObject = (obj) => {
    for (const key in obj) {
      // Verificar si es campo sensible
      if (sensitiveFields.some(field => key.toLowerCase().includes(field.toLowerCase()))) {
        obj[key] = '***REDACTED***';
      } 
      // Recursi칩n para objetos anidados
      else if (obj[key] && typeof obj[key] === 'object') {
        sanitizeObject(obj[key]);
      }
    }
  };

  sanitizeObject(sanitized);
  return sanitized;
};

// Configuraci칩n en Express
const express = require('express');
const app = express();

// Middleware para generar ID 칰nico para cada solicitud
app.use(requestId);

// Middleware de logging
app.use(loggerMiddleware);

// Middleware para manejar errores
app.use((err, req, res, next) => {
  // Loguear error con nivel adecuado
  const statusCode = err.statusCode || 500;
  const logLevel = statusCode >= 500 ? 'error' : 'warn';

  logger[logLevel](`Error: ${err.message}`, {
    requestId: req.id,
    userId: req.user?.id,
    method: req.method,
    path: req.originalUrl,
    statusCode,
    errorCode: err.errorCode,
    stack: process.env.NODE_ENV === 'production' ? undefined : err.stack
  });

  // Continuar con el manejador de errores principal
  next(err);
});

// Exportar logger para uso en toda la aplicaci칩n
module.exports = { logger, sanitizeData };

// Ejemplo de uso en un controlador
const createUser = async (req, res, next) => {
  try {
    logger.debug('Iniciando creaci칩n de usuario', {
      requestId: req.id,
      // Sanitizar datos sensibles antes de loguear
      payload: sanitizeData(req.body)
    });

    // L칩gica de creaci칩n de usuario
    const user = await User.create(req.body);

    logger.info('Usuario creado exitosamente', {
      requestId: req.id,
      userId: user.id
    });

    res.status(201).json({ status: 'success', data: { user } });
  } catch (error) {
    next(error);
  }
};

// Validaci칩n de datos en Express con express-validator
const express = require('express');
const { body, param, query, validationResult } = require('express-validator');

const app = express();
app.use(express.json());

// Middleware para manejar errores de validaci칩n
const validate = validations => {
  return async (req, res, next) => {
    // Ejecutar todas las validaciones
    await Promise.all(validations.map(validation => validation.run(req)));

    const errors = validationResult(req);
    if (errors.isEmpty()) {
      return next();
    }

    // Formatear errores de validaci칩n
    const extractedErrors = errors.array().map(err => ({
      field: err.param,
      message: err.msg,
      value: err.value
    }));

    return res.status(400).json({
      status: 'error',
      code: 'VALIDATION_ERROR',
      message: 'Datos de entrada inv치lidos',
      errors: extractedErrors
    });
  };
};

// Validaciones para registro de usuario
const userRegistrationValidations = [
  body('email')
    .notEmpty().withMessage('El email es requerido')
    .isEmail().withMessage('Debe proporcionar un email v치lido')
    .normalizeEmail() // Normalizar email (lowercase, eliminar espacios)
    .custom(async value => {
      // Validaci칩n personalizada: email 칰nico
      const existingUser = await User.findOne({ email: value });
      if (existingUser) {
        throw new Error('Este email ya est치 en uso');
      }
      return true;
    }),

  body('password')
    .notEmpty().withMessage('La contrase침a es requerida')
    .isLength({ min: 8 }).withMessage('La contrase침a debe tener al menos 8 caracteres')
    .matches(/[a-z]/).withMessage('La contrase침a debe contener al menos una letra min칰scula')
    .matches(/[A-Z]/).withMessage('La contrase침a debe contener al menos una letra may칰scula')
    .matches(/[0-9]/).withMessage('La contrase침a debe contener al menos un n칰mero')
    .matches(/[^a-zA-Z0-9]/).withMessage('La contrase침a debe contener al menos un car치cter especial'),

  body('confirmPassword')
    .notEmpty().withMessage('La confirmaci칩n de contrase침a es requerida')
    .custom((value, { req }) => {
      if (value !== req.body.password) {
        throw new Error('Las contrase침as no coinciden');
      }
      return true;
    }),

  body('username')
    .notEmpty().withMessage('El nombre de usuario es requerido')
    .isLength({ min: 3, max: 20 }).withMessage('El nombre de usuario debe tener entre 3 y 20 caracteres')
    .matches(/^[a-zA-Z0-9_]+$/).withMessage('El nombre de usuario solo puede contener letras, n칰meros y guiones bajos')
    .custom(async value => {
      // Validaci칩n personalizada: username 칰nico
      const existingUser = await User.findOne({ username: value });
      if (existingUser) {
        throw new Error('Este nombre de usuario ya est치 en uso');
      }
      return true;
    }),

  body('name')
    .optional()
    .isLength({ max: 100 }).withMessage('El nombre completo no puede superar los 100 caracteres'),

  body('birthDate')
    .optional()
    .isISO8601().withMessage('La fecha de nacimiento debe estar en formato YYYY-MM-DD')
    .toDate() // Convertir a objeto Date
    .custom(value => {
      // Validar mayor칤a de edad
      const today = new Date();
      const birthDate = new Date(value);
      let age = today.getFullYear() - birthDate.getFullYear();
      const monthDiff = today.getMonth() - birthDate.getMonth();

      if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
        age--;
      }

      if (age < 18) {
        throw new Error('Debes ser mayor de 18 a침os para registrarte');
      }
      return true;
    })
];

// Validaciones para actualizaci칩n de producto (ejemplo de validaci칩n PUT/PATCH)
const productUpdateValidations = [
  param('id')
    .isMongoId().withMessage('ID de producto inv치lido'),

  body('name')
    .optional()
    .notEmpty().withMessage('El nombre no puede estar vac칤o')
    .isLength({ max: 200 }).withMessage('El nombre no puede superar los 200 caracteres'),

  body('price')
    .optional()
    .isFloat({ min: 0.01 }).withMessage('El precio debe ser un n칰mero mayor a 0')
    .toFloat(), // Convertir a n칰mero

  body('description')
    .optional()
    .isLength({ max: 2000 }).withMessage('La descripci칩n no puede superar los 2000 caracteres'),

  body('category')
    .optional()
    .isMongoId().withMessage('ID de categor칤a inv치lido')
    .custom(async value => {
      // Verificar que la categor칤a existe
      const category = await Category.findById(value);
      if (!category) {
        throw new Error('La categor칤a seleccionada no existe');
      }
      return true;
    }),

  body('tags')
    .optional()
    .isArray().withMessage('Los tags deben ser un array')
    .custom(value => {
      if (!Array.isArray(value)) return false;
      // Verificar que cada tag es un string y no est치 vac칤o
      return value.every(tag => typeof tag === 'string' && tag.trim().length > 0);
    }).withMessage('Cada tag debe ser texto no vac칤o'),

  body('stock')
    .optional()
    .isInt({ min: 0 }).withMessage('El stock debe ser un n칰mero entero no negativo')
    .toInt() // Convertir a entero
];

// Validaciones para par치metros de consulta en listado
const productListValidations = [
  query('page')
    .optional()
    .isInt({ min: 1 }).withMessage('El n칰mero de p치gina debe ser un entero positivo')
    .toInt(),

  query('limit')
    .optional()
    .isInt({ min: 1, max: 100 }).withMessage('El l칤mite debe ser un entero entre 1 y 100')
    .toInt(),

  query('sort')
    .optional()
    .isIn(['price_asc', 'price_desc', 'name_asc', 'name_desc', 'newest'])
    .withMessage('Criterio de ordenaci칩n no v치lido'),

  query('minPrice')
    .optional()
    .isFloat({ min: 0 }).withMessage('El precio m칤nimo debe ser un n칰mero no negativo')
    .toFloat(),

  query('maxPrice')
    .optional()
    .isFloat({ min: 0 }).withMessage('El precio m치ximo debe ser un n칰mero no negativo')
    .toFloat()
    .custom((value, { req }) => {
      // Validar que maxPrice > minPrice si ambos est치n presentes
      if (req.query.minPrice && parseFloat(value) <= parseFloat(req.query.minPrice)) {
        throw new Error('El precio m치ximo debe ser mayor que el precio m칤nimo');
      }
      return true;
    })
];

// Rutas con validaci칩n
app.post('/api/users/register', validate(userRegistrationValidations), userController.register);

app.put('/api/products/:id', validate(productUpdateValidations), productController.update);

app.get('/api/products', validate(productListValidations), productController.list);

// Exportar middleware de validaci칩n para uso en toda la aplicaci칩n
module.exports = { validate };
