examples.code:
# Ejemplo de configuración de replicación multi-región para S3 con Terraform

# Bucket principal en región us-east-1
resource "aws_s3_bucket" "primary" {
  bucket = "my-app-data-primary"
  region = "us-east-1"
}

# Bucket de réplica en región us-west-2
resource "aws_s3_bucket" "replica" {
  bucket = "my-app-data-replica"
  region = "us-west-2"
}

# Política de replicación
resource "aws_s3_bucket_replication_configuration" "replication" {
  role   = aws_iam_role.replication.arn
  bucket = aws_s3_bucket.primary.id

  rule {
    id     = "entire-bucket"
    status = "Enabled"

    destination {
      bucket        = aws_s3_bucket.replica.arn
      storage_class = "STANDARD"
    }
  }
}

# Para bases de datos, ejemplo con RDS:
resource "aws_db_instance" "primary" {
  engine            = "mysql"
  instance_class    = "db.r5.large"
  allocated_storage = 100
  identifier        = "primary-db"
  multi_az          = true  # Alta disponibilidad en la región primaria
  # ... otras configuraciones
}

# Réplica de lectura cross-región
resource "aws_db_instance" "replica" {
  replicate_source_db = aws_db_instance.primary.identifier
  instance_class      = "db.r5.large"
  identifier          = "dr-replica-db"
  availability_zone   = "us-west-2a"
  # ... otras configuraciones
}

# Ejemplo de configuración de failover con AWS Route 53 usando Terraform

# Política de comprobación de estado para el entorno primario
resource "aws_route53_health_check" "primary" {
  fqdn              = "api.example.com"
  port              = 443
  type              = "HTTPS"
  resource_path     = "/health"
  failure_threshold = 3
  request_interval  = 30

  tags = {
    Name = "primary-health-check"
  }
}

# Registro DNS primario
resource "aws_route53_record" "primary" {
  zone_id = aws_route53_zone.main.zone_id
  name    = "api.example.com"
  type    = "A"
  set_identifier = "primary"
  failover_routing_policy {
    type = "PRIMARY"
  }
  health_check_id = aws_route53_health_check.primary.id
  alias {
    name                   = aws_lb.primary.dns_name
    zone_id                = aws_lb.primary.zone_id
    evaluate_target_health = true
  }
}

# Registro DNS secundario
resource "aws_route53_record" "secondary" {
  zone_id = aws_route53_zone.main.zone_id
  name    = "api.example.com"
  type    = "A"
  set_identifier = "secondary"
  failover_routing_policy {
    type = "SECONDARY"
  }
  alias {
    name                   = aws_lb.secondary.dns_name
    zone_id                = aws_lb.secondary.zone_id
    evaluate_target_health = true
  }
}

# Para bases de datos, AWS RDS Multi-AZ con promoción automática:
resource "aws_db_instance" "main" {
  # ... otras configuraciones
  multi_az = true
  availability_zone = "us-east-1a"
  backup_retention_period = 7
  deletion_protection = true
  auto_minor_version_upgrade = true
}
